import { getUser } from '../utilities/auth';

export default class UserProfile extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            profile: {}, 
            err: false
        }
    }
     
    async componentDidMount() {
        const response = await getUser();
        if (profile == err) {
            this.setState({
                err: true
            });  
        }
        this.setState({
            profile: response
        });
    }
    render() {
        return (
            <div> 
                <pre> {JSON.stringify(this.state.profile)} </pre> 
            </div>
        )
    }
}
