import axios from 'axios';

export const submitCredentials = async ({ email, password }) => {
    const { data } = await axios.post('/auth/login', { email, password }); 
}

export const getUser = async () => {
    try {
        return { data } = await axios.get('/user/profile');
    } catch(e) {
        console.log(e)
    }   
}
